function jp_checkInView(){try{for(let e=0;e<sections.length;e++){let i=window.innerHeight*activeScreenPercentage,n=window.innerHeight*(1-activeScreenPercentage);sections[e].getBoundingClientRect().top<i&&sections[e].getBoundingClientRect().bottom>=n?(sections[e].classList.add("is-inView"),sections[e].classList.add("touched-inView")):sections[e].classList.remove("is-inView")}}catch(e){console.log(e)}}const activeScreenPercentage=.8,sections=document.querySelectorAll(".inView");jp_checkInView(),window.addEventListener("scroll",jp_checkInView,{passive:!0}),window.addEventListener("resize",jp_checkInView,{passive:!0});